import React from 'react';

const Contact = () => {
  return (
    <>
      <section className="contactus-section">
        <div className="container">
          <div className="row">
            <div className="col-12 col-lg-10 mx-auto">
              <div className="row">
                <div className="contact-leftside col-12 col-lg-5">
                  <h1 className="main-heading fw-bold">
                    Connect With Our <br /> Sales Team.
                  </h1>
                  <p className='main-hero-para'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Et dignissimos in ad? Esse dicta voluptas natus a sapiente ipsa eligendi eius obcaecati corrupti.</p>
                  <figure>
                    <img src="./images/hero1.jpg" alt="contact image" className="img-fluid" />
                  </figure>
                </div>

                {/* right side portion */}

                <div className="col-12 col-lg-7">
                  <form method="post">
                    <div className="row mb-3">

                      <div className="col-12 col-lg-6">

                        <label for="exampleFormControlInput1" className="form-label">First Name</label>
                        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="First Name" />

                      </div>
                      <div className="col-12 col-lg-6">

                        <label for="exampleFormControlInput1" className="form-label">Last Name</label>
                        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="Last Name" />
                      </div>
                    </div>


                    {/* 2nd row */}

                    <div className="row mb-3">

                      <div className="col-12 col-lg-6">

                        <label for="exampleFormControlInput1" className="form-label">Email</label>
                        <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="Enter Your Email" />

                      </div>
                      <div className="col-12 col-lg-6">

                        <label for="exampleFormControlInput1" className="form-label">Phone Number</label>
                        <input type="number" className="form-control" id="exampleFormControlInput1" placeholder="Enter Your Phone Number" />
                      </div>
                    </div>

                    <div className="row">
                      <div className="col-12">
                        <input type="text" 
                        name=""
                        id=""
                        className="form-control"
                        placeholder="enter Your Message"
                         />

                      </div>
                    </div>

                    <div class="form-check form-checkbox-style">
  <input class="form-check-input" type="checkbox" value="" id="flexCheckChecked"/>
  <label class="form-check-label" for="flexCheckChecked">
    Checked checkbox
  </label>
</div>
                  </form>
                </div>
              </div>



            </div>
          </div>
        </div>
      </section>
    </>
  )
}

export default Contact;
